const { verifyResult2, jsxlDirect } = require('../jsxlfunction');
let input, filter, error, output;

// explain the test approach for the modifier
describe('Test some modifer 1', () => {

	// explain the test perspective for this test group 1
	xit('Test some modifier 1 in some way 1', () => {
		// explain the purpose of the test case
		// in as many lines as necessary
		// to provide sufficient understanding and appreciation 
		input = {
			a: 7
		};
		filter = {
			a: { $type: Number, $eq: 7 } 			
		};
		output = {
			a: 7
		};
		verifyResult2('Test some modifier 1 in some way 1 with some input 1', jsxlDirect(input, filter), undefined, output);

		// explain the purpose of the test
		// in as many lines as necessary
		// to provide sufficient understanding and appreciation 
		input = {
			a: 8
		};
		filter = {
			a: { $type: Number, $eq: 7 } 			
    	};
		error = '(Runtime) input.a must be equal to 7';
		verifyResult2('Test some modifier 1 in some way 1 with some input 2', jsxlDirect(input, filter), error);
    })

	// explain the test perspective for this test group 2
	xit('Test some modifier 1 in some way 2', () => {
		// explain the purpose of the test case
		// in as many lines as necessary
		// to provide sufficient understanding and appreciation 
		input = {
			a: 7
		};
		filter = {
			a: { $eq: 7 } 			
		};
		output = {
			a: 7
		};
		verifyResult2('Test some modifier 1 in some way 2 with some input 1', jsxlDirect(input, filter), undefined, output);

		// explain the purpose of the test
		// in as many lines as necessary
		// to provide sufficient understanding and appreciation 
		input = {
			a: '7'
		};
		filter = {
			a: { $eq: 7 } 			
		};
		output = {
			a: '7'
		};
		verifyResult2('Test some modifier 1 in some way 2 with some input 2', jsxlDirect(input, filter), undefined, output);
	})
})